import p5 from 'p5'
import { sample, getRandomArbitrary } from '../prototypes/utilities'

const canvasWidth = 500
const canvasHeight = 700

const Y_AXIS = 1
const X_AXIS = 2

const gradientColors = [
  '#FFA4EC',
  '#FFFFFA',
  '#FFEC84',
  '#751C20',
  '#4FA99D',
  '#01529D',
  '#011A31',
  '#C6AFFC',
  '#ABE1B4',
  '#FDAB5C',
  '#1EA6CB'
]
let c1, c2, c3, c4, c5
let randomColor, randomColor1, randomColor2, randomColor3, randomColor4, randomColor5

function sketch(p) {
  p.setup = () => {
    const canvas = p.createCanvas(canvasWidth, canvasHeight)
    canvas.parent('prototype_45')
    p.background(0)

    randomColor = p.random(gradientColors.length)
    randomColor = p.floor(randomColor)

    c1 = p.color(gradientColors[randomColor])
    c2 = p.color(gradientColors[randomColor])
    c3 = p.color(gradientColors[randomColor])
    c4 = p.color(gradientColors[randomColor])
    c5 = p.color(gradientColors[randomColor])

    p.noLoop()
  }

  p.draw = () => {
    // Background
    setGradient(0, 0, canvasWidth, canvasHeight / 4, b1, b2, Y_AXIS)
    setGradient(
      0,
      canvasHeight / 4,
      canvasWidth,
      canvasHeight / 4,
      c1,
      c2,
      Y_AXIS
    )
    setGradient(
      0,
      canvasHeight / 2,
      canvasWidth,
      canvasHeight / 4,
      c2,
      c3,
      Y_AXIS
    )
    setGradient(
      0,
      (canvasHeight / 4) * 3,
      canvasWidth,
      canvasHeight / 4,
      c3,
      c4,
      Y_AXIS
    )
  }

  function setGradient(x, y, w, h, c1, c2, axis) {
    p.noFill()

    if (axis === Y_AXIS) {
      // Top to bottom gradient
      for (let i = y; i <= y + h; i++) {
        let inter = p.map(i, y, y + h, 0, 1)
        let c = p.lerpColor(c1, c2, inter)
        p.stroke(c)
        p.line(x, i, x + w, i)
      }
    } else if (axis === X_AXIS) {
      // Left to right gradient
      for (let i = x; i <= x + w; i++) {
        let inter = p.map(i, x, x + w, 0, 1)
        let c = p.lerpColor(c1, c2, inter)
        p.stroke(c)
        p.line(i, y, i, y + h)
      }
    }
  }
}

document.addEventListener('DOMContentLoaded', () => {
  new p5(sketch)
})
