// RASTERIZE IMAGE

import p5 from 'p5'
import html2canvas from 'html2canvas'
import { getRandomArbitrary, generateHash } from '../prototypes/utilities'

import image1Url from '../../assets/images/deepface-dark-green-black.png'

let img, 
    colorFG = '#111111',
    colorBG = '#f1f1f1';




function sketch(p) {
  let tileSize = p.random(10,20);
  p.preload = () => {
    img = p.loadImage(image1Url);
  }

  p.setup = () => {
    const canvas = p.createCanvas(500, 264)
    canvas.parent('prototype_47')
    p.background(0)
    p.image(img, 0, 0)
    img.resize(p.width, 0)
  }

  p.draw = () => {
    p.background(colorBG);
    p.fill(colorFG);
    p.noStroke();
     const ratio = 600/600;
     //let coefZ = (p.mouseX/p.width)*200;

     //console.log(coefZ)



     let tilesX = p.width / tileSize;
     tilesX = p.floor(tilesX)
     let tilesY = p.width / tileSize;
     tilesY = p.floor(tilesY)


     //const tilesY = ratio * tilesX;

     for (let y = 0; y < tilesY; y += tileSize) {
       for (let x = 0; x < tilesX; x += tileSize) {
         //let c = img.get(x, y);
         //let b = p.map(p.brightness(c), 0, 255, 1, 0);
         p.fill(255)
         p.push();
         p.translate(x, y);
         //p.rect(0, 0, b * tileSize, b * tileSize);
         p.ellipseMode(p.CORNER)
         p.ellipse(0, 0, tileSize, tileSize)
         p.pop();
       }
     }
  }
}

document.addEventListener('DOMContentLoaded', () => {
  new p5(sketch)
})
