// wave gradient

import p5 from 'p5'
import { sample, getRandomArbitrary } from '../prototypes/utilities'

const canvasSize = 600
const diaMin = 50
const diaMax = 600
const diaStep = 45

function sketch(p) {
  p.setup = () => {
    const canvas = p.createCanvas(p.windowWidth, canvasSize)
    canvas.parent('prototype_40')
    p.noFill()
    p.strokeWeight(diaStep/4)

    //p.background(0)
  }

  p.draw = () => {
    p.background(255)
    for (let dia = diaMin; dia < diaMax; dia+=diaStep) {
      p.ellipse(p.width/2, p.height/2, dia, dia)
      p.ellipse(p.width-p.mouseX, p.height/2, dia, dia)
    }
  }
}

document.addEventListener('DOMContentLoaded', () => {
  new p5(sketch)
})
