    for (let q = 100; q > 0; q-=5) {
      let angle = p.sin(p.radians(i*r+q+p.frameCount*3))*q;
      let x = p.sin(p.radians(i+angle))*(q*2);
      let y = p.cos(p.radians(i+angle))*(q*2);
      p.strokeWeight(2);  
      p.stroke(q*4%360, 255, 255);
      p.line(x, y, -x, -y);
      p.line(y, x, -y, -x);
