// wave thing

import p5 from 'p5'
import { sample, getRandomArbitrary } from '../prototypes/utilities'

const canvasSize = 700
const r = 50

let xx, yy

function sketch(p) {
  p.setup = () => {
    const canvas = p.createCanvas(canvasSize, canvasSize)
    canvas.parent('prototype_41')
    p.noStroke()

  }

  p.draw = () => {
    p.background(250)

  p.translate(p.width/2, p.height/2)
  //p.rotate(p.frameCount*.01)
	//p.scale(2)

  for (let i = 0; i < 360; i+=180) {
    for (let q = 100; q > 0; q-=2) {
      let angle = p.sin(p.radians(i*r+q+p.frameCount))*q
      let x = p.sin(p.radians(i+angle))*(q*2)
      let y = p.sin(p.radians(i+angle))*(q*2);
      p.strokeWeight(1) 
      p.stroke(255, 0, 100)
      p.line(x, y, -x, -y)
      p.line(y, x, -y, -x)
    }
  }

  }
}

document.addEventListener('DOMContentLoaded', () => {
  new p5(sketch)
})
