// hypno circles

import p5 from 'p5'
import { sample, getRandomArbitrary } from '../prototypes/utilities'

let container

const canvas = document.getElementById('calendar');
const ctx = canvas.getContext('2d');

// Set canvas width and height
canvas.width = 300;
canvas.height = 300;

// Define calendar properties
const daysOfWeek = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
const monthNames = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];
const currentDate = new Date();
let currentMonth = currentDate.getMonth();
let currentYear = currentDate.getFullYear();

// Draw calendar
function drawCalendar() {
  // Clear canvas
  ctx.clearRect(0, 0, canvas.width, canvas.height);

  // Draw month and year
  const monthYear = monthNames[currentMonth] + ' ' + currentYear;
  ctx.font = '20px Arial';
  ctx.fillText(monthYear, 50, 30);

  // Draw days of week
  ctx.font = '16px Arial';
  for (let i = 0; i < daysOfWeek.length; i++) {
    ctx.fillText(daysOfWeek[i], i * 40 + 10, 60);
  }

  // Get number of days in current month
  const numDays = new Date(currentYear, currentMonth + 1, 0).getDate();

  // Calculate starting day of month
  const firstDay = new Date(currentYear, currentMonth, 1).getDay();

  // Draw calendar days
  let x = firstDay * 40 + 10;
  let y = 90;
  for (let i = 1; i <= numDays; i++) {
    ctx.fillText(i, x, y);
    x += 40;
    if ((i + firstDay) % 7 === 1) {
      x = 10;
      y += 30;
    }
  }
}

