// GRADIENTS
import p5 from 'p5';
import { sample, getRandomArbitrary } from '../prototypes/utilities';
var clearCanvas = false;
var canvasWidth = 500;
var canvasHeight = 700;
var Y_AXIS = 1;
var X_AXIS = 2;
var gradientColors = ['#FFA4EC', '#FFFFFA', '#FFEC84', '#751C20', '#4FA99D', '#01529D', '#011A31', '#C6AFFC', '#ABE1B4', '#FDAB5C', '#1EA6CB'];
var c1, c2, c3, c4, c5;
var randomColor, randomColor1, randomColor2, randomColor3, randomColor4, randomColor5;

function sketch(p) {
  p.setup = function () {
    var canvas = p.createCanvas(canvasWidth, canvasHeight);
    canvas.parent('prototype_45');
    p.background(0);
    randomColor1 = p.random(gradientColors.length);
    randomColor1 = p.floor(randomColor1);
    randomColor2 = p.random(gradientColors.length);
    randomColor2 = p.floor(randomColor2);
    randomColor3 = p.random(gradientColors.length);
    randomColor3 = p.floor(randomColor3);
    randomColor4 = p.random(gradientColors.length);
    randomColor4 = p.floor(randomColor4);
    randomColor5 = p.random(gradientColors.length);
    randomColor5 = p.floor(randomColor5);
    c1 = p.color(gradientColors[randomColor1]);
    c2 = p.color(gradientColors[randomColor2]);
    c3 = p.color(gradientColors[randomColor3]);
    c4 = p.color(gradientColors[randomColor4]);
    c5 = p.color(gradientColors[randomColor5]); //p.noLoop()
  };

  p.draw = function () {
    setGradient(0, 0, canvasWidth, canvasHeight / 4, c1, c2, Y_AXIS);
    setGradient(0, canvasHeight / 4, canvasWidth, canvasHeight / 4, c2, c3, Y_AXIS);
    setGradient(0, canvasHeight / 2, canvasWidth, canvasHeight / 4, c3, c4, Y_AXIS);
    setGradient(0, canvasHeight / 4 * 3, canvasWidth, canvasHeight / 4, c4, c5, Y_AXIS);

    if (clearCanvas) {
      p.fill(255);
      p.rect(0, 0, 200, 200);
      clearCanvas = false;
    }
  };

  function setGradient(x, y, w, h, c1, c2, axis) {
    p.noFill();

    if (axis === Y_AXIS) {
      // Top to bottom gradient
      for (var i = y; i <= y + h; i++) {
        var inter = p.map(i, y, y + h, 0, 1);
        var c = p.lerpColor(c1, c2, inter);
        p.stroke(c);
        p.line(x, i, x + w, i);
      }
    } else if (axis === X_AXIS) {
      // Left to right gradient
      for (var _i = x; _i <= x + w; _i++) {
        var _inter = p.map(_i, x, x + w, 0, 1);

        var _c = p.lerpColor(c1, c2, _inter);

        p.stroke(_c);
        p.line(_i, y, _i, y + h);
      }
    }
  }

  function changeGradient() {
    randomColor1 = p.random(gradientColors.length);
    randomColor1 = p.floor(randomColor1);
    randomColor2 = p.random(gradientColors.length);
    randomColor2 = p.floor(randomColor2);
    randomColor3 = p.random(gradientColors.length);
    randomColor3 = p.floor(randomColor3);
    randomColor4 = p.random(gradientColors.length);
    randomColor4 = p.floor(randomColor4);
    randomColor5 = p.random(gradientColors.length);
    randomColor5 = p.floor(randomColor5);
    c1 = p.color(gradientColors[randomColor1]);
    c2 = p.color(gradientColors[randomColor2]);
    c3 = p.color(gradientColors[randomColor3]);
    c4 = p.color(gradientColors[randomColor4]);
    c5 = p.color(gradientColors[randomColor5]);
  }

  var changeGradientButton = document.createElement('div');
  changeGradientButton.classList.add('changeGradientButton');
  changeGradientButton.innerText = 'Change Gradient';
  document.body.appendChild(changeGradientButton);
  changeGradientButton.addEventListener('click', function () {
    console.log('changeGradientButton');
    changeGradient();
  });
}

var resetButton = document.createElement('div');
resetButton.classList.add('resetButton');
resetButton.innerText = 'Clear';
document.body.appendChild(resetButton);
resetButton.addEventListener('click', function () {
  clearCanvas = true;
});
document.addEventListener('DOMContentLoaded', function () {
  new p5(sketch);
});
