import p5 from 'p5'
import { sample, getRandomArbitrary } from '../prototypes/utilities'

const canvasWidth = 500
const canvasHeight = 700

const Y_AXIS = 1
const X_AXIS = 2
let b1, b2, c1, c2

function sketch(p) {
  p.setup = () => {
    const canvas = p.createCanvas(canvasWidth, canvasHeight)
    canvas.parent('prototype_45')
    p.background(0)

    b1 = p.color(255)
    b2 = p.color(0)
    c1 = p.color(204, 102, 0)
    c2 = p.color(0, 102, 153)

    p.noLoop()
  }

  p.draw = () => {
    // Background
    setGradient(0, 0, canvasWidth, canvasHeight/2, b1, b2, Y_AXIS)
    setGradient(0, canvasHeight/2, canvasWidth, canvasHeight, b2, b1, Y_AXIS)
    // Foreground
    setGradient(50, 90, 540, 80, c1, c2, Y_AXIS)
    setGradient(50, 190, 540, 80, c2, c1, X_AXIS)
  }

  function setGradient(x, y, w, h, c1, c2, axis) {
    p.noFill()

    if (axis === Y_AXIS) {
      // Top to bottom gradient
      for (let i = y; i <= y + h; i++) {
        let inter = p.map(i, y, y + h, 0, 1)
        let c = p.lerpColor(c1, c2, inter)
        p.stroke(c)
        p.line(x, i, x + w, i)
      }
    } else if (axis === X_AXIS) {
      // Left to right gradient
      for (let i = x; i <= x + w; i++) {
        let inter = p.map(i, x, x + w, 0, 1)
        let c = p.lerpColor(c1, c2, inter)
        p.stroke(c)
        p.line(i, y, i, y + h)
      }
    }


  }
}



document.addEventListener('DOMContentLoaded', () => {
  new p5(sketch)
})
