// flower

import p5 from 'p5'
import { sample, getRandomArbitrary } from '../prototypes/utilities'

const canvasSize = 700
let bubbles = [];

function sketch(p) {
  p.setup = () => {
    const canvas = p.createCanvas(canvasSize, canvasSize)
    canvas.parent('prototype_43')
    //p.mousePressed(appear)
    if (p.mousePressed) {
      appear()
    }

  }

  function appear() {
    let r = p.random(10, 50);
    let b = new Bubble(mouseX, mouseY, r);
    bubbles.push(b);
  }

  p.draw = () => {
    p.background(0);

  for (let bubble of bubbles) {
    bubble.move();
    bubble.show();
  }

  for (let i = 0; i < bubbles.length; i++) {
    bubbles[i].move();
    bubbles[i].show();
  }

  }

  class Bubble {
    constructor(x, y, r) {
      this.x = x;
      this.y = y;
      this.r = r;
    }

    move() {
      this.x = this.x + p.random(-5, 5);
      this.y = this.y + p.random(-5, 5);
    }

    show() {
      p.stroke(255);
      p.strokeWeight(4);
      p.noFill();
      p.ellipse(this.x, this.y, this.r * 2);
    }
  }
}

document.addEventListener('DOMContentLoaded', () => {
  new p5(sketch)
})
