// hypno circles

import p5 from 'p5'
import { sample, getRandomArbitrary } from '../prototypes/utilities'

const canvasSize = 700
let tileAmount = 10
let r = canvasSize/tileAmount
let colors = ['#62FFF6', '#FF6287', '#25D96D', '#FF7A00', '#DFFF1D', '#FFFFFF']

function sketch(p) {

  p.setup = () => {
    const canvas = p.createCanvas(canvasSize, canvasSize)
    canvas.parent('prototype_43')
    p.background(255)
    p.noStroke()
    //generateCalendar()
  }

  p.draw = () => {
  }

  function generateCalendar() {

    //let body = document.querySelector('body')
    let calendar = document.createElement('div')
    calendar.setAttribute('id', "month-calendar")
    document.body.appendChild(calendar)

    let month = document.createElement('ul');
    month.classList.add("month")
    calendar.appendChild(month)

    let m = document.createElement('li');
    m.classList.add("month-name")
    month.appendChild(m)

    let y = document.createElement('li');
    y.classList.add("year-name")
    month.appendChild(y)

    let weekdays = document.createElement('ul');
    weekdays.classList.add("weekdays")
    calendar.appendChild(weekdays)

    dayName = ["M", "T", "W", "T", "F", "S", "S"]

    for(i=0;i<7;i++) {
      let li = document.createElement('li')
      li.innerHTML=dayName[i];
      weekdays.appendChild(li);
    }

    let days = document.createElement('ul');
    days.classList.add("days")
    calendar.appendChild(days)

    date = ["1", "2", "...", "31"]

    for(i=0;i<4;i++) {
      let li = document.createElement('li')
      li.innerHTML=date[i];
      days.appendChild(li);
    }

    const prev = document.createElement('div')
      prev.classList.add('prev')
      prev.innerText = 'Предыдущий'
      calendar.appendChild(prev)

      const next = document.createElement('div')
      next.classList.add('next')
      next.innerText = 'Следующий'
      calendar.appendChild(next)

          let nowDate = new Date(),
            nowDateNumber = nowDate.getDate(),
            nowMonth = nowDate.getMonth(),
            nowYear = nowDate.getFullYear(),
            container = document.getElementById("month-calendar"),
            monthContainer = container.getElementsByClassName("month-name")[0],
            yearContainer = container.getElementsByClassName("year-name")[0],
            daysContainer = container.getElementsByClassName("days")[0],
            // prev = container.getElementsByClassName("prev")[0],
            // next = container.getElementsByClassName("next")[0],
            monthName = [
              "JANUARY",
              "FEBRUARY",
              "MARCH",
              "APRIL",
              "MAY",
              "JUNE",
              "JULY",
              "AUGUST",
              "SEPTEMBER",
              "OCTOBER",
              "NOVEMBER",
              "DECEMBER",
            ];

          let curDate = nowDate.setMonth(nowDate.getMonth() - 1);
          console.log(nowDate.getFullYear());

          function setMonthCalendar(year, month) {
            let monthDays = new Date(year, month + 1, 0).getDate(),
              monthPrefix = new Date(year, month, 0).getDay(),
              monthDaysText = "";

            monthContainer.textContent = monthName[month];
            yearContainer.textContent = year;
            daysContainer.innerHTML = "";

            if (monthPrefix > 0) {
              for (let i = 1; i <= monthPrefix; i++) {
                monthDaysText += "<li></li>";
              }
            }

            for (let i = 1; i <= monthDays; i++) {
              monthDaysText += "<li>" + i + "</li>";
            }

            daysContainer.innerHTML = monthDaysText;

            if (month == nowMonth && year == nowYear) {
              days = daysContainer.getElementsByTagName("li");
              days[monthPrefix + nowDateNumber - 1].classList.add("date-now");
            }
          }

          setMonthCalendar(nowYear, nowMonth);

          prev.onclick = function () {
            let curDate = new Date(
              yearContainer.textContent,
              monthName.indexOf(monthContainer.textContent)
            );

            curDate.setMonth(curDate.getMonth() - 1);

            let curYear = curDate.getFullYear(),
              curMonth = curDate.getMonth();

            setMonthCalendar(curYear, curMonth);
          };

          next.onclick = function () {
            let curDate = new Date(
              yearContainer.textContent,
              monthName.indexOf(monthContainer.textContent)
            );

            curDate.setMonth(curDate.getMonth() + 1);

            let curYear = curDate.getFullYear(),
              curMonth = curDate.getMonth();

            setMonthCalendar(curYear, curMonth);
          };

        }
}

document.addEventListener('DOMContentLoaded', () => {
  new p5(sketch)
  //generateCalendar()
})
